from pulic.API import request_Api

'''15. account_createCode'''


def createCode(api_name, params):
	'''
	部署合约
	:param api_name: "account_createAccount"
	:param params:部署合约的地址;合约内容;金额;gas价格;gas上限
	:return: 合约地址
	示例代码
	curl -H "Content-Type: application/json" -X post --data '{"jsonrpc":"2.0","method":"account_createCode","params":["0x3ebcbe7cb440dd8c52940a2963472380afbb56c5","","0x111","0x110","0x30000"],"id":1}' http://127.0.0.1:15645
	'''
	try:
		result = request_Api(api_name, params)
		print("部署合约成功，返回值为{}".format(result))
		return result
	except Exception as e:
		print("部署合约失败，api返回错误，返回值为{}".format(e))
		return -1


if __name__ == '__main__':
	api_name = "account_createCode"
	params = ["0x3ebcbe7cb440dd8c52940a2963472380afbb56c5","","0x111","0x110","0x30000"]
	createCode(api_name, params)