import logging
from pulic.API import request_Api
from smart_contract.log import set_log_cfg

'''account_createCode'''

def createCode(api_name, params):
	'''
	部署合约
	:param api_name: account_readContract
	:param params: 部署合约的地址;合约内容（在remix编译后deploy执行后的input的二进制文件）;金额(部署智能合约时金额必须为0x0否则会部署失败);gas价格;gas上限
	:return: 合约地址
	curl -H "Content-Type: application/json" -X post --data '{"jsonrpc":"2.0","method":"account_createCode","params":["0x3ebcbe7cb440dd8c52940a2963472380afbb56c5","","0x111","0x110","0x30000"],"id":1}' http://127.0.0.1:15645
	响应：
	{"jsonrpc":"2.0","id":1,"result":"0x9a8d8d5d7d00bbe0eb1b9431a13a7219008e352241b751b177bfb29e4e75b0d1"}
	'''
	try:
		result = request_Api(api_name, params)
		print("部署合约{}".format(result))
		logging.info("部署合约{}".format(result))
		return result
	except Exception as e:
		print("部署合约失败{}".format(e))
		logging.error("部署合约失败{}".format(e))
		return e


if __name__ == '__main__':
	set_log_cfg(default_path="log_conf.yaml")
	api_name = "account_createCode"
	#合约地址：0x80e61888252ba164e14ddbf4de888ccbf167967a
	params = ["0x80e61888252ba164e14ddbf4de888ccbf167967a",
	           "",
	           "0x0", "0x110", "0x300000"]
	createCode(api_name, params)